// Test service greeter api via grpc client
func Test_service_greeter_methods(t *testing.T) {
	conn := getRPCClientConnForTest()
	cli := helloworldV1.NewGreeterClient(conn)
	ctx, _ := context.WithTimeout(context.Background(), time.Second*5)

	tests := []struct {
		name    string
		fn      func() (interface{}, error)
		wantErr bool
	}{

		{
			name: "SayHello",
			fn: func() (interface{}, error) {
				// todo type in the parameters before testing
				req := &helloworldV1.HelloRequest{
					Name: "46546546465",
				}

				return cli.SayHello(ctx, req)
			},
			wantErr: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := tt.fn()
			if (err != nil) != tt.wantErr {
				t.Errorf("test '%s' error = %v, wantErr %v", tt.name, err, tt.wantErr)
				return
			}
			data, _ := json.MarshalIndent(got, "", "    ")
			fmt.Println(string(data))
		})
	}
}

